import { ErrorDialogComponent } from './../../shared/components/error-dialog/error-dialog.component';
import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { catchError, Observable, of } from 'rxjs';
import { CoursesService } from '../services/courses.service';

import { Course } from './../model/course';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.scss']
})
export class CoursesComponent implements OnInit {

  courses$: Observable<Course[]>;
  displayedColumns = ['_id','name','category','actions'];


  constructor(
    private coursesService: CoursesService,
    public dialog: MatDialog,
    private router: Router,
    private route: ActivatedRoute) {

    this.courses$ = this.coursesService.listCoursesAll()
    .pipe(
      catchError(error =>{
        this.onError('Erro ao carregar cursos');
        return of([])
      })
      );

  }

  onError(errorMsg: string) {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMsg
    })};


  ngOnInit(): void {
  }

  onAdd(){
    console.log('onAdd');
    this.router.navigate( ['new'], {relativeTo: this.route});
  }

}
;
<mat-card>
  <mat-toolbar color="primary">Cursos Disponíveis</mat-toolbar>

  <div *ngIf="courses$ | async as courses; else loading">
    <table mat-table [dataSource]="courses" class="mat-elevation-z8">

      <!-- ID Column -->
      <ng-container matColumnDef="_id">
        <th mat-header-cell *matHeaderCellDef>ID</th>
        <td mat-cell *matCellDef="let courses">{{ courses._id }}</td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef>Nome do Curso</th>
        <td mat-cell *matCellDef="let courses">{{ courses.name }}</td>
      </ng-container>

      <!-- Category Column -->
      <ng-container matColumnDef="category">
        <th mat-header-cell *matHeaderCellDef >Categoria</th>
        <td mat-cell *matCellDef="let courses">{{ courses.category }}
          <mat-icon aria-hidden="false" aria-label="Category of Course"> {{ courses.category | category }}</mat-icon>
        </td>
      </ng-container>

      <!-- Actions Column -->
      <ng-container matColumnDef="actions">
        <mat-header-cell *matHeaderCellDef>
          <button mat-mini-fab color="accent" aria-label="Adicionar um curso">
            <mat-icon (click)="onAdd()">add</mat-icon>
          </button>
        </mat-header-cell>

        <mat-cell *matCellDef="let course">
          <button mat-icon-button color="primary" aria-label="Editar Curso">
            <mat-icon>edit</mat-icon>
          </button>

          <button mat-icon-button color="warn" aria-label="Remover Curso">
            <mat-icon>delete</mat-icon>
          </button>
        </mat-cell>

      </ng-container>


      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
    </table>
  </div>

  <ng-template #loading>
    <div class="loading-spiner">
      <mat-spinner></mat-spinner>
    </div>
  </ng-template>
</mat-card>
